"use strict";
var signin = new Vue({
    el: "#container",
    data: {
        seen: !1,
        form: {
            name: "",
            pass: "",
            checked: ""
        }
    },
    methods: {
        showWindow: function () {
            this.seen = !this.seen
        },
        signin: function () {
          var s = this;
          if(this.form.name && this.form.pass){ 
            axios.post("/api/v1/userSignIn", {
                username: this.form.name,
                password: this.form.pass,
                checked: this.form.checked
            }).then(function (e) {
                  console.log(e)
                if(e.data.msgCode===200){
                  location.reload()
                }else{
                  s.$message({
                    message: e.data.msg,
                    type: "warning",
                    showClose: !0,
                    duration: 2e3
                })
                }
            }).
            catch (function (s) {
                console.log(s)
            })
          }else{ s.$message({
                message: "账号或者密码不能为空",
                type: "warning",
                showClose: !0,
                duration: 2e3
            })
          }
        },
        signup: function () {
            var s = this;
            this.form.name && this.form.pass ? axios.post("/signup", {
                username: this.form.name,
                password: this.form.pass
            }).then(function (e) {
                "注册成功" == e.data ? s.$message("注册成功,请点击登陆") : s.$message.error("该用户名已存在")
            })
                .
            catch (function (s) {
                console.log(s)
            }) : this.$message({
                message: "账号或者密码不能为空",
                type: "warning",
                showClose: !0,
                duration: 2e3
            })
        }
    },
    created: function () {
        document.querySelector(".window").style.display = "block"
    }
});